class Clock {
    constructor(debug){
        this.debug = debug
    }
    /**
     * Returns performance.now in seconds.
     */
    tick(){
        return (performance.now() / 1000)
    }
    /**
     * Yields until a given time threshold is reached.
     *
     * @param   {number} s Number in seconds to wait.
     * @returns {true}
     */
    sleep(s){
        let start = performance.now()
        s = (s * 1000)

        while ((Math.abs((start - performance.now())) < s)) {
        }

        return true
    }
}

module.exports = new Clock;